import React, { useState, useEffect } from 'react';
import { Card, CardHeader, CardTitle, CardContent } from '@/components/ui/card';
import { Tabs, TabsList, TabsTrigger, TabsContent } from '@/components/ui/tabs';
import { useGame } from '../../context/GameContext';
import { motion } from 'framer-motion';

const ScoreBoard = () => {
  const { state } = useGame();

  return (
    <div className="bg-white rounded-lg shadow p-4">
      <h2 className="text-xl font-bold mb-4">Scoreboard</h2>
      
      <div className="space-y-4">
        {state.gameMode === 'multi' ? (
          <div className="grid grid-cols-2 gap-4">
            {Object.values(state.players).map(player => (
              <div key={player.id} 
                   className="p-4 bg-gray-50 rounded-lg text-center">
                <h3 className="font-bold">{player.name}</h3>
                <p>Score: {player.score}</p>
                <p>Matches: {player.matches}</p>
              </div>
            ))}
          </div>
        ) : (
          <div className="space-y-2">
            {state.highScores?.map((score, index) => (
              <div key={index} 
                   className="flex justify-between p-2 bg-gray-50 rounded">
                <span>{score.playerName}</span>
                <span>{score.score}</span>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
};

const GameBoard = () => {
  const { state, dispatch } = useGame();

  return (
    <motion.div 
      className="grid grid-cols-4 gap-4 p-8"
      initial={{ opacity: 0 }}
      animate={{ opacity: 1 }}
    >
      {state.cards.map(card => (
        <Card
          key={card.id}
          card={card}
          isSelected={state.selectedCards.some(c => c.id === card.id)}
          onSelect={() => dispatch({ type: 'SELECT_CARD', payload: card })}
        />
      ))}
    </motion.div>
  );
};

export default ScoreBoard; // Only one default export
