import { useGame } from '../../context/GameContext';
import { motion } from 'framer-motion';
import { useEffect } from 'react';

const Timer = () => {
  const { state, dispatch } = useGame();
  
  useEffect(() => {
    let interval;
    if (state.isPlaying) {
      interval = setInterval(() => {
        if (state.turnTimer <= 0) {
          dispatch({ type: 'SWITCH_PLAYER' });
        } else {
          dispatch({ type: 'UPDATE_TIMER' });
        }
      }, 1000);
    }
    return () => clearInterval(interval);
  }, [state.isPlaying, state.turnTimer]);

  return (
    <motion.div 
      animate={{ scale: state.turnTimer <= 5 ? [1, 1.1, 1] : 1 }}
      transition={{ repeat: state.turnTimer <= 5 ? Infinity : 0 }}
      className={`text-2xl font-bold ${state.turnTimer <= 5 ? 'text-red-500' : ''}`}
    >
      {state.turnTimer}s
    </motion.div>
  );
};

export default Timer;
