// src/components/GameBoard/Card.tsx
import { motion, AnimatePresence } from 'framer-motion';
import { Card as CardType } from '../../types/game';

interface CardProps {
  card: CardType;
  isSelected: boolean;
  onSelect: () => void;
}

export const Card: React.FC<CardProps> = ({ card, isSelected, onSelect }) => {
  return (
    <div className="card-container">
      <motion.div
        className={`card-inner ${isSelected || card.matched ? 'flipped' : ''}`}
        initial={false}
        animate={{ 
          rotateY: isSelected || card.matched ? 180 : 0,
          scale: card.matched ? [1, 1.1, 1] : 1
        }}
        transition={{
          rotateY: {
            duration: 0.4, // Reduced from 0.8
            type: "spring",
            stiffness: 300, // Increased from 150
            damping: 25,   // Adjusted from 15
            bounce: 0.2    // Added slight bounce
          },
          scale: {
            duration: 0.3, // Reduced from 0.5
            ease: "backOut"
          }
        }}
        whileHover={{ scale: 1.05 }}
        whileTap={{ scale: 0.95 }}
        onClick={() => !card.matched && !isSelected && onSelect()}
      >
        {/* Front Face */}
        <div className="card-face front">
          <div className="w-full h-full bg-gradient-to-br from-violet-900/90 to-indigo-900/90
                       border-2 border-cyan-500/50 rounded-xl
                       flex items-center justify-center
                       shadow-[0_0_20px_rgba(6,182,212,0.4)]
                       hover:shadow-[0_0_30px_rgba(6,182,212,0.6)]
                       hover:border-cyan-400 
                       backdrop-blur-sm
                       transition-all duration-300">
            <motion.span 
              className="text-5xl text-cyan-400 font-bold relative z-10"
              animate={{ 
                textShadow: [
                  "0 0 8px #00fff0, 0 0 12px #00fff0",
                  "0 0 15px #00fff0, 0 0 20px #00fff0"
                ]
              }}
              transition={{
                duration: 2,
                repeat: Infinity,
                repeatType: "reverse"
              }}
            >
              ?
            </motion.span>
          </div>
        </div>

        {/* Back Face */}
        <div className="card-face back">
          <div className={`
            w-full h-full rounded-xl flex items-center justify-center
            backdrop-blur-sm transition-all duration-300
            ${card.matched 
              ? 'bg-gradient-to-br from-emerald-900/90 to-teal-900/90 border-2 border-green-400/50 shadow-[0_0_25px_rgba(34,197,94,0.6)]' 
              : 'bg-gradient-to-br from-cyan-900/90 to-blue-900/90 border-2 border-cyan-400/50 shadow-[0_0_25px_rgba(6,182,212,0.6)]'
            }`}>
            <motion.span 
              className={`text-5xl font-bold ${card.matched ? 'text-green-400' : 'text-cyan-400'}`}
              initial={{ scale: 0, rotate: -180 }}
              animate={{ 
                scale: 1,
                rotate: 0,
                textShadow: card.matched 
                  ? ["0 0 8px #00ff00", "0 0 15px #00ff00"]
                  : ["0 0 8px #00fff0", "0 0 15px #00fff0"]
              }}
              transition={{ type: "spring", stiffness: 200, damping: 15 }}
            >
              {card.value}
            </motion.span>
          </div>
        </div>

        {/* Particle Effects */}
        <AnimatePresence>
          {card.matched && (
            <motion.div
              initial={{ opacity: 1 }}
              animate={{ opacity: 0 }}
              exit={{ opacity: 0 }}
              transition={{ duration: 1 }}
              className="absolute inset-0 pointer-events-none"
            >
              {[...Array(6)].map((_, i) => (
                <motion.div
                  key={i}
                  className="absolute w-2 h-2 bg-green-400 rounded-full"
                  initial={{ x: "50%", y: "50%", scale: 0 }}
                  animate={{ 
                    x: `${50 + Math.cos(i * 60) * 100}%`,
                    y: `${50 + Math.sin(i * 60) * 100}%`,
                    scale: [1, 0]
                  }}
                  transition={{ duration: 0.8, ease: "easeOut" }}
                />
              ))}
            </motion.div>
          )}
        </AnimatePresence>
      </motion.div>
    </div>
  );
};
