import { motion } from 'framer-motion';
import { useContext } from 'react';
import { GameContext } from '../../context/GameContext';
import { Card as CardType } from '../../types/game';

interface CardProps {
  card: CardType;
  isSelected: boolean;
  onSelect: () => void;
}

export const Card: React.FC<CardProps> = ({ card, isSelected, onSelect }) => {
  const { state } = useContext(GameContext);
  const { currentPlayer } = state;

  const getPlayerStyles = () => {
    if (card.matched) {
      return {
        background: 'from-emerald-900/90 to-teal-900/90',
        border: 'border-green-500/50',
        shadow: 'shadow-[0_0_20px_rgba(34,197,94,0.4)]',
        text: 'text-green-400',
        glow: ['0 0 8px #00ff00, 0 0 12px #00ff00', '0 0 15px #00ff00, 0 0 20px #00ff00']
      };
    }
    
    return currentPlayer === 1 ? {
      background: 'from-cyan-900/90 to-blue-900/90',
      border: 'border-cyan-500/50',
      shadow: 'shadow-[0_0_20px_rgba(6,182,212,0.4)]',
      text: 'text-cyan-400',
      glow: ['0 0 8px #00fff0, 0 0 12px #00fff0', '0 0 15px #00fff0, 0 0 20px #00fff0']
    } : {
      background: 'from-violet-900/90 to-indigo-900/90', 
      border: 'border-purple-500/50',
      shadow: 'shadow-[0_0_20px_rgba(147,51,234,0.4)]',
      text: 'text-purple-400',
      glow: ['0 0 8px #a855f7, 0 0 12px #a855f7', '0 0 15px #a855f7, 0 0 20px #a855f7']
    };
  };

  const styles = getPlayerStyles();

  return (
    <div className="card-container" onClick={() => !card.matched && !isSelected && onSelect()}>
      <div className={`card-inner ${isSelected || card.matched ? 'flipped' : ''}`}>
        {/* Front Face */}
        <div className="card-face front">
          <div className={`
            w-full h-full bg-gradient-to-br ${styles.background}
            border-2 ${styles.border} rounded-xl
            flex items-center justify-center
            ${styles.shadow} transition-all duration-300
          `}>
            <motion.span 
              className={`text-5xl font-bold ${styles.text}`}
              animate={{ 
                textShadow: styles.glow
              }}
              transition={{
                duration: 1.5,
                repeat: Infinity,
                repeatType: "reverse"
              }}
            >
              ?
            </motion.span>
          </div>
        </div>

        {/* Back Face */}
        <div className="card-face back">
          <motion.div 
            className={`
              w-full h-full rounded-xl flex items-center justify-center
              ${card.matched 
                ? 'bg-gradient-to-br from-emerald-900/90 to-teal-900/90 border-2 border-green-400/50' 
                : 'bg-gradient-to-br from-cyan-900/90 to-blue-900/90 border-2 border-cyan-400/50'
              }
            `}
            animate={{
              opacity: card.matched ? 0.5 : 1,
              scale: card.matched ? 0.95 : 1,
            }}
            transition={{
              duration: 0.3,
              ease: "easeOut"
            }}
          >
            <motion.span 
              className={`text-5xl font-bold ${card.matched ? 'text-green-400/70' : 'text-cyan-400'}`}
            >
              {card.value}
            </motion.span>
          </motion.div>
        </div>
      </div>
    </div>
  );
};
