import React, { createContext, useContext, useReducer } from 'react';

const GameContext = createContext();

const initialState = {
  cards: [],
  currentPlayer: 1,
  scores: {
    player1: 0,
    player2: 0
  },
  gameStatus: 'idle',
  flippedCards: [],
  matchedPairs: [],
  attempts: 0
};

function gameReducer(state, action) {
  switch (action.type) {
    case 'START_GAME':
      return {
        ...initialState,
        cards: action.payload,
        gameStatus: 'playing'
      };
    
    case 'FLIP_CARD':
      return {
        ...state,
        flippedCards: [...state.flippedCards, action.payload],
        attempts: state.flippedCards.length === 1 ? state.attempts + 1 : state.attempts
      };
    
    case 'CHECK_MATCH':
      const [first, second] = state.flippedCards;
      const isMatch = first.value === second.value;
      
      return {
        ...state,
        matchedPairs: isMatch ? [...state.matchedPairs, first.value] : state.matchedPairs,
        scores: isMatch ? {
          ...state.scores,
          [`player${state.currentPlayer}`]: state.scores[`player${state.currentPlayer}`] + 1
        } : state.scores,
        currentPlayer: isMatch ? state.currentPlayer : state.currentPlayer === 1 ? 2 : 1,
        flippedCards: []
      };
    
    case 'END_GAME':
      return {
        ...state,
        gameStatus: 'finished'
      };
    
    default:
      return state;
  }
}

export function GameProvider({ children }) {
  const [state, dispatch] = useReducer(gameReducer, initialState);

  return (
    <GameContext.Provider value={{ state, dispatch }}>
      {children}
    </GameContext.Provider>
  );
}

export function useGame() {
  const context = useContext(GameContext);
  if (!context) {
    throw new Error('useGame must be used within a GameProvider');
  }
  return context;
}
