import { useGame } from '../../context/GameContext';
import { motion } from 'framer-motion';
import { Card } from './Card'; 

const GameBoard = () => {
  const { state, dispatch } = useGame();

  return (
    <div className="space-y-8">
      {state.gameMode === 'multi' && (
        <div className="multiplayer-container">
          {Object.values(state.players).map(player => (
            <motion.div
              key={player.name}
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              className={`player-info ${
                state.currentPlayer === player.id 
                  ? 'border-cyan-400/60' 
                  : ''
              }`}
            >
              <h3 className="text-xl font-bold mb-4 neon-text">{player.name}</h3>
              <div className="grid grid-cols-2 gap-4 text-center">
                <div>
                  <p className="text-cyan-400">Matches</p>
                  <p className="text-2xl">{player.matches}</p>
                </div>
                <div>
                  <p className="text-cyan-400">Score</p>
                  <p className="text-2xl">{player.score}</p>
                </div>
              </div>
            </motion.div>
          ))}
        </div>
      )}

      <motion.div 
        className="grid grid-cols-4 gap-6 p-6 max-w-3xl mx-auto"
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
      >
        {state.cards.map(card => (
          <Card
            key={card.id}
            card={card}
            isSelected={state.selectedCards.some(c => c.id === card.id)}
            onSelect={() => dispatch({ type: 'SELECT_CARD', payload: card })}
          />
        ))}
      </motion.div>
    </div>
  );
};

export default GameBoard;
