import React, { useState } from 'react';
import { Card, CardHeader, CardTitle, CardContent } from '../ui/card';
import { Tabs, TabsList, TabsTrigger, TabsContent } from '../ui/tabs';
import { useGame } from '../../context/GameContext';
import { motion } from 'framer-motion';

const ScoreBoard = () => {
  const { state } = useGame();
  const [activeTab, setActiveTab] = useState('single');
  
  const scores = state.highScores?.[activeTab] || [];

  return (
    <Card>
      <CardHeader>
        <CardTitle>High Scores - {activeTab === 'single' ? 'Single Player' : 'Multiplayer'}</CardTitle>
      </CardHeader>
      <CardContent>
        <Tabs defaultValue={activeTab} onValueChange={setActiveTab}>
          <TabsList>
            <TabsTrigger value="single">Single Player</TabsTrigger>
            <TabsTrigger value="multi">Multiplayer</TabsTrigger>
          </TabsList>
          
          <TabsContent value={activeTab}>
            {scores.map((score, index) => (
              <div key={index} className="flex justify-between p-2 bg-gray-50 rounded mb-2">
                <span>{score.playerName}</span>
                <span>{score.score}</span>
              </div>
            ))}
          </TabsContent>
        </Tabs>
      </CardContent>
    </Card>
  );
};

export default ScoreBoard;
