import React, { useEffect } from 'react';
import { useGame } from '../../context/GameContext';
import { Card } from '@/components/ui/card';

const GameBoard = () => {
  const { state, dispatch } = useGame();
  const { cards, flippedCards, matchedPairs, gameStatus } = state;

  const handleCardClick = (card) => {
    if (
      gameStatus !== 'playing' ||
      flippedCards.length === 2 ||
      matchedPairs.includes(card.value) ||
      flippedCards.find(c => c.id === card.id)
    ) {
      return;
    }

    dispatch({ type: 'FLIP_CARD', payload: card });

    if (flippedCards.length === 1) {
      setTimeout(() => {
        dispatch({ type: 'CHECK_MATCH' });
      }, 1000);
    }
  };

  return (
    <div className="grid grid-cols-4 gap-4 p-4 max-w-4xl mx-auto">
      {cards.map((card) => (
        <Card
          key={card.id}
          onClick={() => handleCardClick(card)}
          className={`
            h-24 cursor-pointer transition-all duration-300 transform
            ${flippedCards.find(c => c.id === card.id) || matchedPairs.includes(card.value)
              ? 'bg-white rotate-y-180'
              : 'bg-blue-500'
            }
            ${matchedPairs.includes(card.value) ? 'opacity-60' : ''}
          `}
        >
          <div className="w-full h-full flex items-center justify-center">
            <span className="text-4xl">
              {(flippedCards.find(c => c.id === card.id) || matchedPairs.includes(card.value))
                ? card.value
                : ''}
            </span>
          </div>
        </Card>
      ))}
    </div>
  );
};

export default GameBoard;
